import React, { useState, useMemo, useCallback, useRef, useEffect } from 'react';
import { Language, Theme, LogEntry, Task } from './types';
import Header from './components/Header';
import TimeTracker from './components/TimeTracker';
import LogManager from './components/LogManager';
import ProfileModal from './components/ProfileModal';
import LogFormModal from './components/LogFormModal';
import ReportModal from './components/ReportModal';
import { useUserData } from './hooks/useUserData';
import LoadingSpinner from './components/LoadingSpinner';
import Reminders from './components/Reminders';
import Sidebar from './components/Sidebar';
import TourGuide, { TourStep } from './components/TourGuide';
import useLocalStorage from './hooks/useLocalStorage';
import WorkHoursChart from './components/WorkHoursChart';
import InstructionsModal from './components/InstructionsModal';
import AboutModal from './components/AboutModal';
import { ToastMessage } from './components/Toast';

type SoundKey = 'click' | 'clockIn' | 'clockOut' | 'delete' | 'success' | 'error';

interface MainAppProps {
  user: any | null; // Firebase User or null for guest
  onLogout: () => void;
  language: Language;
  setLanguage: (lang: Language) => void;
  theme: Theme;
  setTheme: (theme: Theme) => void;
  t: (key: any) => string;
  showToast: (message: string, type?: 'success' | 'error' | 'info', icon?: React.ReactNode) => void;
  toasts: ToastMessage[];
}

const MainApp: React.FC<MainAppProps> = ({ user, onLogout, language, setLanguage, theme, setTheme, t, showToast, toasts }) => {
  const { logs, profile, tasks, loadingData, addLog, saveLog, deleteLog, saveProfile, saveTask, deleteTask } = useUserData(user);

  const [isProfileModalOpen, setIsProfileModalOpen] = useState(false);
  const [isLogFormModalOpen, setIsLogFormModalOpen] = useState(false);
  const [isReportModalOpen, setIsReportModalOpen] = useState(false);
  const [isInstructionsModalOpen, setIsInstructionsModalOpen] = useState(false);
  const [isAboutModalOpen, setIsAboutModalOpen] = useState(false);
  const [logToEdit, setLogToEdit] = useState<LogEntry | null>(null);

  const [tourCompleted, setTourCompleted] = useLocalStorage('saati-tour-completed-v1', false);
  const [runTour, setRunTour] = useState(!tourCompleted);
  const [isSidebarCollapsed, setIsSidebarCollapsed] = useLocalStorage('saati-sidebar-collapsed', false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  
  const mainContentRef = useRef<HTMLDivElement>(null);

  // --- Sound Effects System ---
  const sounds = useMemo(() => ({
    click: new Audio('data:audio/mpeg;base64,'),
    clockIn: new Audio('data:audio/mpeg;base64,... this is...'),
    clockOut: new Audio('data:audio/mpeg;base-64,'),
    delete: new Audio('data:audio/mpeg;base64,'),
    success: new Audio('data:audio/mpeg;base64,'),
    error: new Audio('data:audio/mpeg;base64,'),
  }), []);

  const playSound = useCallback((sound: SoundKey) => {
    if (profile.soundEffectsEnabled) {
      const audio = sounds[sound];
      if (audio) {
        audio.currentTime = 0; // Rewind to the start
        audio.play().catch(error => console.error(`Error playing sound: ${sound}`, error));
      }
    }
  }, [profile.soundEffectsEnabled, sounds]);
  
  // Play sounds for toasts
  const processedToastIds = useRef(new Set());
  useEffect(() => {
    toasts.forEach(toast => {
      if (!processedToastIds.current.has(toast.id)) {
        if (toast.type === 'success' || toast.type === 'info') {
          playSound('success');
        } else if (toast.type === 'error') {
          playSound('error');
        }
        processedToastIds.current.add(toast.id);
      }
    });
  }, [toasts, playSound]);


  const handleOpenLogForm = (log?: LogEntry) => {
    setLogToEdit(log || null);
    setIsLogFormModalOpen(true);
  };

  const handleLinkClick = (href: string) => {
    const element = document.querySelector(href);
    if (element) {
        element.scrollIntoView({ behavior: 'smooth', block: 'start' });
    }
  };

  const tourSteps: TourStep[] = [
    { target: '#dashboard', content: t('tourStep1'), position: 'bottom' },
    { target: '#analytics', content: t('tourStep2'), position: 'top' },
    { target: '#logs-manager', content: t('tourStep3'), position: 'top' },
    { target: '#reminders', content: t('tourStep4'), position: 'top' },
    { target: '#tour-trigger', content: t('tourStep5'), position: 'right' },
  ];

  const toggleSidebar = () => {
    playSound('click');
    setIsSidebarCollapsed(!isSidebarCollapsed);
  }

  // Intersection Observer for scroll animations
  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add('is-visible');
            observer.unobserve(entry.target);
          }
        });
      },
      { threshold: 0.1 }
    );

    const sections = mainContentRef.current?.querySelectorAll('.section-animate');
    sections?.forEach((section) => observer.observe(section));

    return () => {
      sections?.forEach((section) => observer.unobserve(section));
    };
  }, [loadingData]);


  if (loadingData) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <LoadingSpinner />
      </div>
    );
  }

  return (
    <div className={`flex h-screen bg-slate-50 dark:bg-slate-950 transition-all duration-300 ${isSidebarCollapsed ? 'sm:ps-0' : 'sm:ps-64'}`}>
      <Sidebar 
        t={t}
        onHelpClick={() => setRunTour(true)}
        onInstructionsClick={() => setIsInstructionsModalOpen(true)}
        onAboutClick={() => setIsAboutModalOpen(true)}
        isCollapsed={isSidebarCollapsed}
        language={language}
        onLinkClick={handleLinkClick}
        isMobileMenuOpen={isMobileMenuOpen}
        setIsMobileMenuOpen={setIsMobileMenuOpen}
      />
      <div className="flex-1 flex flex-col h-screen">
        <Header
          user={user}
          onLogout={onLogout}
          onProfileClick={() => setIsProfileModalOpen(true)}
          language={language}
          setLanguage={setLanguage}
          theme={theme}
          setTheme={setTheme}
          t={t}
          toggleSidebar={toggleSidebar}
          isSidebarCollapsed={isSidebarCollapsed}
          setIsMobileMenuOpen={setIsMobileMenuOpen}
          playSound={playSound}
        />
        <main ref={mainContentRef} className="smooth-scroll flex-1 p-4 sm:p-6 lg:p-8">
          <div className="max-w-7xl mx-auto flex flex-col gap-8">
            <section id="dashboard" className="section-animate">
              <TimeTracker logs={logs} addLog={addLog} profile={profile} t={t} showToast={showToast} language={language} playSound={playSound} />
            </section>
            <section id="analytics" className="section-animate">
                <WorkHoursChart logs={logs} profile={profile} t={t} language={language}/>
            </section>
            <section id="logs-and-tasks" className="section-animate grid grid-cols-1 lg:grid-cols-5 gap-8 items-start">
              <div id="logs-manager" className="lg:col-span-3 h-[80vh]">
                <LogManager
                  logs={logs}
                  profile={profile}
                  onAdd={() => handleOpenLogForm()}
                  onEdit={handleOpenLogForm}
                  onDelete={deleteLog}
                  onGenerateReport={() => setIsReportModalOpen(true)}
                  t={t}
                  language={language}
                  showToast={showToast}
                  playSound={playSound}
                />
              </div>
              <div id="reminders" className="lg:col-span-2 h-[80vh]">
                <Reminders tasks={tasks} saveTask={saveTask} deleteTask={deleteTask} t={t} showToast={showToast} playSound={playSound} />
              </div>
            </section>
          </div>
        </main>
      </div>

      <ProfileModal
        isOpen={isProfileModalOpen}
        onClose={() => setIsProfileModalOpen(false)}
        settings={profile}
        onSave={saveProfile}
        t={t}
        showToast={showToast}
        playSound={playSound}
      />
      <LogFormModal
        isOpen={isLogFormModalOpen}
        onClose={() => setIsLogFormModalOpen(false)}
        onSave={saveLog}
        logToEdit={logToEdit}
        t={t}
        showToast={showToast}
      />
      <ReportModal
        isOpen={isReportModalOpen}
        onClose={() => setIsReportModalOpen(false)}
        logs={logs}
        profile={profile}
        t={t}
        language={language}
        user={user}
      />
       <InstructionsModal
        isOpen={isInstructionsModalOpen}
        onClose={() => setIsInstructionsModalOpen(false)}
        t={t}
      />
       <AboutModal
        isOpen={isAboutModalOpen}
        onClose={() => setIsAboutModalOpen(false)}
        t={t}
      />
      <TourGuide run={runTour} setRun={(shouldRun) => {
          setRunTour(shouldRun);
          if(!shouldRun) {
              setTourCompleted(true);
          }
      }} steps={tourSteps} t={t} />
    </div>
  );
};

export default MainApp;
